import React, { useState } from 'react';
import { getProductImage, handleImageError } from '../utils/imageUtils';

const ProductImage = ({ 
  product, 
  imageIndex = 0, 
  size = '300x200', 
  className = 'product-image',
  style = {},
  onClick,
  alt,
  showLoadingState = false
}) => {
  const [imageError, setImageError] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  const handleError = (e) => {
    setImageError(true);
    handleImageError(e, product?.category, size);
  };

  const handleLoad = () => {
    setImageLoaded(true);
  };

  const imageSrc = getProductImage(product?.images, imageIndex, size, product?.category);
  const imageAlt = alt || product?.name || 'Product image';

  return (
    <div style={{ position: 'relative', ...style }}>
      {showLoadingState && !imageLoaded && !imageError && (
        <div 
          className="image-loading"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: '#f8f9fa',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#6c757d',
            fontSize: '12px'
          }}
        >
          Loading...
        </div>
      )}
      <img
        src={imageSrc}
        alt={imageAlt}
        className={`${className} ${imageError ? 'image-error' : ''}`}
        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        onClick={onClick}
        onError={handleError}
        onLoad={handleLoad}
        loading="lazy"
      />
    </div>
  );
};

export default ProductImage;
